	$(".plus").on("click",function(){
		if($(this).parent("div").attr("disabled") == "disabled") return;
		var o = $("#ts" + $(this).attr("data"));
		o.focus();
		var reg =  /^\d+$/;
		var num = o.val();
		(reg.test(num) && num >= 0) ? o.val(num * 1 + 1) : o.val(0);
		o.blur();
	});
	
	$(".minus").on("click",function(){
		if($(this).parent("div").attr("disabled") == "disabled") return;
		var o = $("#ts" + $(this).attr("data"));
		o.focus();
		var reg =  /^\d+$/;
		var num = o.val();
		(reg.test(num) && num >= 1) ? o.val(--num) : o.val(0);
		o.blur();
	});
	
