	function in_array(searchv,array){
		for(var i in array){
			if(array[i]==searchv){
				return i;
			}
		}
		return -1;
	}
	
	$(".uinfo .inptxt").blur(function(){
		var o =$(this);
		
		var arr = [ "username", "pass", "rpass", "code" ];
		var t = o.attr("data");
		var v = o.val();
		
		var tipinfo = "";
		switch(in_array(t,arr)){
			case "-1":
				return;
				break;
			case "0":
				var reg =  /^[a-zA-Z_]{1}[0-9a-zA-Z_]{1,}$/;
				if(!reg.test(v)){
					tipinfo = "用户名必须由字母、数字或\"_\"组成,且首位不能是数字！";
				}
				else if(v.length < 6){
					tipinfo = "用户名长度不能小于6！";
				}
				else if(v.length > 30){
					tipinfo = "用户名长度不能大于30！";
				}
				break;
			case "1":
				if(v.length < 6){
					tipinfo = "密码长度不能小于6！";
				}
				break;
			case "2":
				if(v != $("#password").val()){
					tipinfo = "确认密码与密码不一致！";
				}
				break;
			case "3":
				if(v.trim() == ""){
					tipinfo = "验证码不能为空！";
				}
				break;
				
		}
		if(tipinfo != ""){
			o.css("border-color","#f00");
			o.parent("p").next(".wrongtip").html(tipinfo).show();
		}
		else{
			o.css("border-color","#cfcdc7");
			o.parent("p").next(".wrongtip").hide();
		}
	});
	
